import java.util.*;
public class Recap3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int tablenumber = 5;
		int count = 10;
				
		for ( int i = 1; i <= count ; i = i+1) {
			
			int z = tablenumber * i;
			System.out.println(tablenumber + " times" + " " + i + " equals" + " " + z);
		}
		
		
		

	}

}
